package types

import (
	"crypto/rand"
	"github.com/core-coin/go-core/common"
	"github.com/core-coin/go-core/rlp"
	eddsa "github.com/core-coin/go-goldilocks"
	"math/big"
	"testing"

	"github.com/core-coin/go-core/params"

	"github.com/core-coin/go-core/crypto"
)

func TestNucleusSigning(t *testing.T) {
	key, _ := crypto.GenerateKey(rand.Reader)
	pub := eddsa.Ed448DerivePublicKey(*key)
	addr := crypto.PubkeyToAddress(pub)

	signer := NewNucleusSigner(params.AllCryptoreProtocolChanges.NetworkID)
	tx, err := SignTx(NewTransaction(0, addr, new(big.Int), 0, new(big.Int), nil), signer, key)
	if err != nil {
		t.Fatal(err)
	}

	from, err := Sender(signer, tx)
	if err != nil {
		t.Fatal(err)
	}
	if from != addr {
		t.Errorf("exected from and address to be equal. Got %x want %x", from, addr)
	}
}

func TestNucleusSigningDecoding(t *testing.T) {
	for i, test := range []struct {
		txRlp, addr string
	}{
		{"", "cb979f85b68cc4365531562bbfe8594d13221d6677b9"},
		{"", "cb9169fd913782244a964124dfa86f65c084fca25f4a"},
		{"", "cb585688591fcc640af17b49e8345801f48c270c761d"},
		{"", "cb787b9cca8e2c23de58ba6adf9240ad52ed8e6042a1"},
		{"", "cb03fb18ef1250b2987fb83f401d732acf28955e6238"},
		{"", "cb4036d36dd98cdaa7c41de2de40d40548a4abce464a"},
		{"", "cb091e23d0b9f7912466fa079ed1f9439b34cd40f448"},
		{"", "cb06b92cb364f7e08dfcf8c1010de3361aaf00ff8f30"},
		{"", "cb9145a5c8329ff959edbec447423eabf596042f6809"},
		{"", "cb329269856d0826f232333f9b4f93ea4283a647894c"},
	} {
		signer := NewNucleusSigner(params.TestChainConfig.NetworkID)
		var tx *Transaction
		err := rlp.DecodeBytes(common.Hex2Bytes(test.txRlp), &tx)
		if err != nil {
			t.Errorf("%d: %v", i, err)
			continue
		}

		from, err := Sender(signer, tx)
		if err != nil {
			t.Errorf("%d: %v", i, err)
			continue
		}

		addr, _ := common.HexToAddress(test.addr)
		if from != addr {
			t.Errorf("%d: expected %x got %x", i, addr, from)
		}
	}
}
