package ed448

import . "gopkg.in/check.v1"

func (s *Ed448Suite) Test_BytesFromHex(c *C) {
	bs := bytesFromHex(
		"e6f5b8ae49cef779e577dc29824eff453f1c4106030088115ea49b4ee8" +
			"4a7b7cdfe06e0d622fc55c7c559ab1f6c3ea3257c07979809026de")
	exp := []byte{
		0xe6, 0xf5, 0xb8, 0xae, 0x49, 0xce, 0xf7, 0x79,
		0xe5, 0x77, 0xdc, 0x29, 0x82, 0x4e, 0xff, 0x45,
		0x3f, 0x1c, 0x41, 0x06, 0x03, 0x00, 0x88, 0x11,
		0x5e, 0xa4, 0x9b, 0x4e, 0xe8, 0x4a, 0x7b, 0x7c,
		0xdf, 0xe0, 0x6e, 0x0d, 0x62, 0x2f, 0xc5, 0x5c,
		0x7c, 0x55, 0x9a, 0xb1, 0xf6, 0xc3, 0xea, 0x32,
		0x57, 0xc0, 0x79, 0x79, 0x80, 0x90, 0x26, 0xde,
	}

	c.Assert(bs, DeepEquals, exp)
}
